import React, { useState, useRef } from 'react';
import {
  Container,
  Box,
  Button,
  Typography,
  Card,
  CardContent,
  IconButton,
  Grid,
  CircularProgress,
  Alert,
  Snackbar,
} from '@mui/material';
import {
  Mic,
  Stop,
  SentimentSatisfied,
  SentimentVerySatisfied,
  Favorite,
} from '@mui/icons-material';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material';
import CssBaseline from '@mui/material/CssBaseline';
import RecordPage from './pages/RecordPage';
import StoriesPage from './pages/StoriesPage';

const API_URL = 'http://localhost:8001';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
});

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      chunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (e) => {
        chunksRef.current.push(e.data);
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(chunksRef.current, { type: 'audio/webm' });
        await handleAudioUpload(audioBlob);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      setError(null);
    } catch (err) {
      console.error('Error accessing microphone:', err);
      setError('Error accessing microphone. Please make sure you have granted microphone permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const handleAudioUpload = async (audioBlob) => {
    setLoading(true);
    setError(null);
    try {
      const formData = new FormData();
      formData.append('file', audioBlob, 'recording.webm');

      const response = await axios.post(`${API_URL}/upload-audio`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const enhancedStory = await axios.post(`${API_URL}/enhance-story`, {
        text: response.data.text,
      });

      setStories([...stories, enhancedStory.data]);
    } catch (err) {
      console.error('Error processing audio:', err);
      setError(err.response?.data?.detail || 'Error processing audio. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReaction = async (storyId, reactionType) => {
    try {
      const response = await axios.post(`${API_URL}/stories/${storyId}/react`, null, {
        params: { reaction_type: reactionType },
      });

      setStories(stories.map(story => 
        story.id === storyId ? response.data : story
      ));
    } catch (err) {
      console.error('Error adding reaction:', err);
      setError('Error adding reaction. Please try again.');
    }
  };

  const handleCloseError = () => {
    setError(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Routes>
          <Route path="/" element={<RecordPage />} />
          <Route path="/stories" element={<StoriesPage />} />
        </Routes>
      </Router>
      <Container maxWidth="md">
        <Box sx={{ my: 4, textAlign: 'center' }}>
          <Typography variant="h3" component="h1" gutterBottom>
            Story Memory
          </Typography>

          <Box sx={{ my: 2 }}>
            <Button
              variant="contained"
              color={isRecording ? 'error' : 'primary'}
              startIcon={isRecording ? <Stop /> : <Mic />}
              onClick={isRecording ? stopRecording : startRecording}
              disabled={loading}
            >
              {isRecording ? 'Stop Recording' : 'Start Recording'}
            </Button>
          </Box>

          {loading && (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 2 }}>
              <CircularProgress />
            </Box>
          )}

          <Snackbar 
            open={!!error} 
            autoHideDuration={6000} 
            onClose={handleCloseError}
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
          >
            <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
              {error}
            </Alert>
          </Snackbar>

          <Grid container spacing={2} sx={{ mt: 2 }}>
            {stories.map((story, index) => (
              <Grid item xs={12} key={index}>
                <Card>
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      Original Story
                    </Typography>
                    <Typography paragraph>{story.text}</Typography>

                    <Typography variant="h6" gutterBottom>
                      Enhanced Story
                    </Typography>
                    <Typography paragraph>{story.enhanced_text}</Typography>

                    <Box sx={{ display: 'flex', gap: 1, mt: 2 }}>
                      <IconButton
                        onClick={() => handleReaction(story.id, 'funny')}
                        color="primary"
                      >
                        <SentimentSatisfied />
                        <Typography variant="caption" sx={{ ml: 0.5 }}>
                          {story.reactions.funny}
                        </Typography>
                      </IconButton>

                      <IconButton
                        onClick={() => handleReaction(story.id, 'happy')}
                        color="primary"
                      >
                        <SentimentVerySatisfied />
                        <Typography variant="caption" sx={{ ml: 0.5 }}>
                          {story.reactions.happy}
                        </Typography>
                      </IconButton>

                      <IconButton
                        onClick={() => handleReaction(story.id, 'emotional')}
                        color="primary"
                      >
                        <Favorite />
                        <Typography variant="caption" sx={{ ml: 0.5 }}>
                          {story.reactions.emotional}
                        </Typography>
                      </IconButton>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
