import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  Typography,
  Card,
  CardContent,
  IconButton,
  Button,
  CircularProgress,
  Alert,
  Snackbar,
  Paper,
} from '@mui/material';
import {
  SentimentSatisfied,
  SentimentVerySatisfied,
  Favorite,
  ArrowBack,
  ArrowForward,
  Mic,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const API_URL = 'http://localhost:8001';

function StoriesPage() {
  const [stories, setStories] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchStories();
  }, []);

  const fetchStories = async () => {
    try {
      const response = await axios.get(`${API_URL}/stories`);
      setStories(response.data);
    } catch (err) {
      console.error('Error fetching stories:', err);
      const errorMessage = err.response?.data?.detail || 'Error loading stories. Please try again.';
      setError(typeof errorMessage === 'string' ? errorMessage : 'Error loading stories. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleReaction = async (storyId, reactionType) => {
    try {
      const response = await axios.post(`${API_URL}/stories/${storyId}/react`, null, {
        params: { reaction_type: reactionType },
      });

      setStories(stories.map(story => 
        story.id === storyId ? response.data : story
      ));
    } catch (err) {
      console.error('Error adding reaction:', err);
      const errorMessage = err.response?.data?.detail || 'Error adding reaction. Please try again.';
      setError(typeof errorMessage === 'string' ? errorMessage : 'Error adding reaction. Please try again.');
    }
  };

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === 0 ? stories.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex === stories.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handleCloseError = () => {
    setError(null);
  };

  const handleRecordNew = () => {
    navigate('/');
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (stories.length === 0) {
    return (
      <Container maxWidth="md">
        <Box sx={{ my: 4, textAlign: 'center' }}>
          <Typography variant="h4" gutterBottom>
            No Stories Yet
          </Typography>
          <Button
            variant="contained"
            startIcon={<Mic />}
            onClick={handleRecordNew}
            sx={{ mt: 2 }}
          >
            Record Your First Story
          </Button>
        </Box>
      </Container>
    );
  }

  const currentStory = stories[currentIndex];

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4, textAlign: 'center' }}>
        <Typography variant="h3" component="h1" gutterBottom>
          Story Collection
        </Typography>

        <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', my: 2 }}>
          <Button
            variant="outlined"
            startIcon={<ArrowBack />}
            onClick={handlePrevious}
            sx={{ mr: 2 }}
          >
            Previous
          </Button>

          <Typography variant="h6" sx={{ mx: 2 }}>
            Story {currentIndex + 1} of {stories.length}
          </Typography>

          <Button
            variant="outlined"
            endIcon={<ArrowForward />}
            onClick={handleNext}
            sx={{ ml: 2 }}
          >
            Next
          </Button>
        </Box>

        <Card sx={{ mt: 4 }}>
          <CardContent>
            <Typography variant="h5" gutterBottom>
              Original Story
            </Typography>
            <Typography paragraph>{currentStory.text}</Typography>

            <Typography variant="h5" gutterBottom>
              Enhanced Version
            </Typography>
            <Typography paragraph>{currentStory.enhanced_text}</Typography>

            <Box sx={{ display: 'flex', gap: 1, mt: 2, justifyContent: 'center' }}>
              <IconButton
                onClick={() => handleReaction(currentStory.id, 'funny')}
                color="primary"
              >
                <SentimentSatisfied />
                <Typography variant="caption" sx={{ ml: 0.5 }}>
                  {currentStory.reactions.funny}
                </Typography>
              </IconButton>

              <IconButton
                onClick={() => handleReaction(currentStory.id, 'happy')}
                color="primary"
              >
                <SentimentVerySatisfied />
                <Typography variant="caption" sx={{ ml: 0.5 }}>
                  {currentStory.reactions.happy}
                </Typography>
              </IconButton>

              <IconButton
                onClick={() => handleReaction(currentStory.id, 'emotional')}
                color="primary"
              >
                <Favorite />
                <Typography variant="caption" sx={{ ml: 0.5 }}>
                  {currentStory.reactions.emotional}
                </Typography>
              </IconButton>
            </Box>
          </CardContent>
        </Card>

        <Button
          variant="contained"
          startIcon={<Mic />}
          onClick={handleRecordNew}
          sx={{ mt: 4 }}
        >
          Record New Story
        </Button>

        <Snackbar 
          open={!!error} 
          autoHideDuration={6000} 
          onClose={handleCloseError}
          anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
        >
          <Alert onClose={handleCloseError} severity="error" sx={{ width: '100%' }}>
            {error}
          </Alert>
        </Snackbar>
      </Box>
    </Container>
  );
}

export default StoriesPage;
